mapper.select:
SELECT memId, teamId, name, ssn, roll, password, age, gender 
FROM MEMBER 

	
		memId LIKE #{memId}
	
	
		AND password LIKE #{password}

mapper.update:
UPDATE MEMBER SET teamId = #{teamId}, roll = #{roll} 

, password = #{password}

WHERE memId LIKE #{memId}

mapper.delete:
DELETE FROM MEMBER
WHERE memId LIKE #{memId}
AND password LIKE #{password}
